import React, { Component } from 'react'
import "../styles/App.css"
import Pokedex from './Pokedex';


// AREA DE TRABALHO --> Substituir pelo JSON
// const pokedex = [
//   {
//     "id": 1,
//     "name": "Bulbasaur",
//     "type": [
//       "Grass",
//       "Poison"
//     ]
//   },
//   {
//     "id": 2,
//     "name": "Ivysaur",
//     "type": [
//       "Grass",
//       "Poison"
//     ]
//   }]
// AREA DE TRABALHO -----------------------------------
const pokedex = require('../pokedex.json')


export default class App extends Component {


  // AREA DE TRABALHO --> Usar estrutura de Class Component
  constructor(props) {
    super(props);
    this.state = {
      pokemons: pokedex,
      pokemon: pokedex[0]
    };
  }
  // AREA DE TRABALHO -------------------------------------------




  update(p) {
    console.log(p);
    this.pokemon = p
    this.setState(this);
    // Nesta função, o Pokémon visível deve ser atualizado
    // Não vamos esquecer que se for uma função, devemos vinculá-la... como podemos evitar o uso do vínculo?
  }

  getPokemon = () =>  this.state.pokemon.id.toString().padStart(3, '0')

  getTypes = () => this.pokemon.type.join(' and ')



  render() {
    /* AREA DE TRABALHO
      Podemos usar a desestruturação ou modificar diretamente as variáveis ​​this.pokemon por this.state ...
    */

    return (
      <div style={{ display: "flex" }}>
        <div className="container">
          <img id="pokemonImg" src={`https://assets.pokemon.com/assets/cms2/img/pokedex/full/${this.getPokemon()}.png`} 
          alt={this.pokemon.name} />
          <p id="name">{`${this.pokemon.name} is a pokemon type ${this.getTypes()}`}</p>
        </div>
        <Pokedex updateParent={this.update.bind(this)} pokemons={this.pokemons} />
      </div>
    )
  }
}
